		var jRename = $('<a rel="delete" href="#"/>').append('<img src="' + gThis._GetImage('Rename') + '" alt="' + GForm.Language.attribute_editor_rename_attribute + '" title="' + GForm.Language.attribute_editor_rename_attribute + '"/>');
